sys_variable_value.value:
(function execute(inputs, outputs) {
	const data = []

	while(inputs.packages.next()){
		let acqSysId = inputs.packages.sys_id
        let currentContractSysId = inputs.packages.current_contract_and_recurring_information.sys_id
		let grContract = new GlideRecord('x_g_dis_atat_current_contract_and_recurring_information')
        grContract.get(currentContractSysId)
     	grContract.acquisition_package = acqSysId
      	grContract.update()

      let obj = {
        acquisitionPackage: acqSysId,
        currentContract: currentContractSysId
      }
      data.push(obj)
	}
  }
  outputs.data = data
})(inputs, outputs);

