element.calculation:
(function calculatedFieldValue(current) {
	var output = '';
    if (current.domain1 && current.domain2) {
        output += current.domain1.getDisplayValue();
        output += ' to ';
        output += current.domain2.getDisplayValue();
    }
    return output;
})(current);

(function calculatedFieldValue(current) {
	var output = '';
    if (current.domain1 && current.domain2) {
        output += current.domain1.toString();
        output += '_TO_';
        output += current.domain2.toString();
    }
    return output;
})(current);
