sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs.payload = JSON.parse(inputs.payload);
  outputs.num_events = inputs.payload.length;
  outputs.num_updates = 0;
  var now = new GlideDateTime();

  inputs.payload.forEach(function(event) {
    var acqPackageId;
    var portfolioId;
    var link;
    var response = event.content.response
    var snowJobId = event.initialSnowRequest.jobId

    // update provisioning job
    var jobRecord = new GlideRecord("x_g_dis_atat_provisioning_job");
    jobRecord.addQuery('hoth_job_id', snowJobId);
    jobRecord.query();

    if (jobRecord.next()) {      
      // handle errors
      if (event.code < 200 || event.code >= 400 ) {
        jobRecord.status = "FAILED";
        jobRecord.status_message = JSON.stringify(response);
        jobRecord.update();
        outputs.num_updates++;
      }

      // status 200 success (after 202 polling - async)
      if (response.status) {
        jobRecord.csp_job_id = response.status.provisioningJobId;
        jobRecord.status = response.status.status;
        jobRecord.update();
        outputs.num_updates++;

        portfolioId = response.status.portfolioId
      }

      // portfolio 200 success (sync)
      if (response.portfolio) {
        // a success should indicate that the job is complete
        jobRecord.status = "COMPLETE";
        jobRecord.update();
        outputs.num_updates++;

        portfolioId = response.portfolio.id;
        link = response.portfolio.dashboardLink;
      }

      // pass on package Id to update portfolio
      acqPackageId = jobRecord.acquisition_package.sys_id.toString();

    } else {
      gs.error("Could not find a Provisioning Job record with HOTH Job ID " + snowJobId);
    }

    // update portfolios
    var portfolioRecord = new GlideRecord("x_g_dis_atat_portfolio");
    portfolioRecord.addQuery("acquisition_package", acqPackageId);
    portfolioRecord.addQuery("provisioned", false);
    portfolioRecord.query();

    if (portfolioRecord.next()) {
      if (event.code === 200) {
        // update the corresponding portfolio
        portfolioRecord.provisioned = true;
        portfolioRecord.provisioned_date = now;
        // TODO: figure out why pending operators are not being reset
        portfolioRecord.pending_operators = ""; // clear operators
        portfolioRecord.csp_portfolio_id = portfolioId;
        portfolioRecord.dashboard_link = link ? link : null;
        portfolioRecord.portfolio_status = "ACTIVE";
        portfolioRecord.update();
      }

      // Q: is this needed or do we want to handle portfolio status updates
      // differently?
      // if (event.code < 200 || event.code >= 400) {
      //   // update the failed portfolio
      //   portfolioRecord.csp_portfolio_id = portfolioId;
      //   portfolioRecord.dashboard_link = link ? link : null;
      //   portfolioRecord.portfolio_status = "ARCHIVED";
      //   portfolioRecord.update();
      // }

    } else {
      gs.error("Could not find a Portfolio record to update that is referenced by Job ID " + snowJobId)
    }

  });
})(inputs, outputs);

