sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs.payload = JSON.parse(inputs.payload);
  outputs.num_events = inputs.payload.length;
  outputs.num_updates = 0;
  var now = new GlideDateTime();

  inputs.payload.forEach(function(event) {
    var acqPackageId;
    var portfolioId;
    var link;
    var response = event.content.response;
    var snowJobId = event.initialSnowRequest.jobId;
    var operationType = event.initialSnowRequest.operationType;

    // update provisioning job
    var jobRecord = new GlideRecord("x_g_dis_atat_provisioning_job");
    jobRecord.addQuery('hoth_job_id', snowJobId);
    jobRecord.query();

    if (jobRecord.next()) {      
      // handle errors
      if (event.code < 200 || event.code >= 400 ) {
        jobRecord.status = "FAILURE";
        jobRecord.status_message = JSON.stringify(response);
        jobRecord.update();
        outputs.num_updates++;
      }

      if (operationType === "ADD_PORTFOLIO") {
        // portfolio 200 success (sync)
        if (response.portfolio && event.code === 200) {
          // a success should indicate that the job is complete
          jobRecord.status = "SUCCESS";
          jobRecord.update();
          outputs.num_updates++;
          portfolioId = response.portfolio.id;
          var portfolioRecord = new GlideRecord("x_g_dis_atat_portfolio");
          portfolioRecord.get(jobRecord.portfolio.sys_id);

          // update the corresponding portfolio
          portfolioRecord.provisioned = true;
          portfolioRecord.provisioned_date = now;
          portfolioRecord.operators = ""; // clear operators
          portfolioRecord.csp_id = portfolioId;
          portfolioRecord.portfolio_status = "ACTIVE";
          portfolioRecord.update();

          // Find all Environments under this Portfolio
          var environmentRecord = new GlideRecord("x_g_dis_atat_environment");
          environmentRecord.addQuery("portfolio", jobRecord.portfolio.sys_id);
          environmentRecord.query();
          while (environmentRecord.hasNext()) {
            environmentRecord.next();
            var hoth = new HothProvisioning(portfolioRecord);
            var payload = hoth.createAddEnvironmentPayload(environmentRecord);

            var newEnvironmentJobRecord = new GlideRecord("x_g_dis_atat_provisioning_job");
            newEnvironmentJobRecord.initialize();
            newEnvironmentJobRecord.setValue("title", environmentRecord.name.toString());
            newEnvironmentJobRecord.setValue("environment", environmentRecord.sys_id.toString());
            newEnvironmentJobRecord.setValue("hoth_job_id", payload.jobId);
            newEnvironmentJobRecord.setValue("job_type", "ADD_ENVIRONMENT");
            newEnvironmentJobRecord.setValue("payload", JSON.stringify(payload));
            newEnvironmentJobRecord.setValue("portfolio", jobRecord.portfolio.sys_id);
            newEnvironmentJobRecord.setValue("status", "NOT_STARTED");
            newEnvironmentJobRecord.insert();
          }
        }
      } else if (operationType === "ADD_ENVIRONMENT") {
        if (event.code === 200) {
          jobRecord.status = "SUCCESS";
          jobRecord.update();
          outputs.num_updates++;
          var environmentRecord = new GlideRecord("x_g_dis_atat_environment");
          environmentRecord.get(jobRecord.environment.sys_id.toString());

          // Handle GetProvisioningStatus response (after async process complete)
          if (response.status) {
            environmentRecord.csp_id = response.status.environmentId;                         
          }
          // Handle regular HTTP 200 response direct from AddEnvironment request
          else if (response.environment) {
            environmentRecord.csp_id = response.environment.id;            
          }
          else {
            throw new ErrorHandler().createError(
              "Expected 'response.status' or 'response.environment' to be non-null",
              new ErrorHandler().METHOD_ERROR
            );               
          }
          environmentRecord.update();     
        }
        else {
          throw new ErrorHandler().createError(
            "Operation Type '" + operationType + "' is not supported.",
            new ErrorHandler().METHOD_ERROR
          );    
        }
      } else if (operationType === "UPDATE_TASK_ORDER") {
        if (event.code === 200) {
          jobRecord.status = "SUCCESS";
          jobRecord.update();
          outputs.num_updates++;
          var taskOrderRecord = new GlideRecord("x_g_dis_atat_task_order");
          taskOrderRecord.get(jobRecord.task_order.sys_id.toString());

          taskOrderRecord.provisioned = true;
          taskOrderRecord.provisioned_date = now;
          taskOrderRecord.update();     
        }
      } else {
        gs.error("Could not find a Provisioning Job record with HOTH Job ID " + snowJobId);
      }

    });
  })(inputs, outputs);

