sys_variable_value.value:
(function execute(inputs, outputs) {
  var package = inputs.package;
  var ctb = convertToBoolean; // for compact execution

  // Tables 
  var projectOverview = package.project_overview;
  var contractAward = package.contract_award;
  // TODO (AT-0000): recreate Current Contract Info table 
  var currentContractInfo = package.current_contract_and_recurring_information;
  var currentEnvironment = package.current_environment;
  var periodOfPerformance = package.period_of_performance;
  var requiredServices = package.required_services;
  var requirementsCostEstimate = package.requirements_cost_estimate;
  var gfeOverview = package.gfe_overview;
  var cc = package.contract_considerations;
  var sensitiveInfo = package.sensitive_information;


  // environment instances 
  var instanceFields = [
    'instance_name', 'classification_level', 'instance_location', 'csp_region', 
    'performance_tier', 'pricing_model', 'pricing_model_expiration', 
    'operating_system_licensing', 'number_of_vcpus', 'storage_type', 'storage_amount', 
    'storage_unit', 'memory_amount', 'memory_unit', 
    'data_egress_monthly_amount', 'data_egress_monthly_unit'
  ]

  var instances = getListRecords(
    currentEnvironment.environment_instances.toString(), 
    instanceFields, 
    'x_g_dis_atat_environment_instance')
    .map(function(instance) { 
      var classification = new global.GlideQuery('x_g_dis_atat_classification_level')
        .get(instance.classification_level, ['classification', 'impact_level'])
        .orElse(null)
      delete classification.sys_id
      instance.classification_level = classification

      return instance
    })
    .map(removeSysId);

  // package periods used for services later
  var packagePeriodsById = mapPackagePeriods(
    periodOfPerformance.base_period, periodOfPerformance.option_periods
  );
  var packageOptionPeriods = periodOfPerformance
  	.option_periods.split(",")
  	.map(function(periodId) { return packagePeriodsById[periodId] });

  // get services and nested data
  var serviceFields = [
    'applicable_classification_levels', 'usage_description', 
    'need_for_entire_to_duration', 'applicable_periods', 
    'select_service_offerings', 'other_service_offering'
  ];
  var services = getListRecords(requiredServices.toString(), 
    serviceFields, 'x_g_dis_atat_required_services')
    .map(removeSysId)
  	.map(getNestedServiceInfo);

  // hoth api 
    var dow_document_data = {
      award_effective_date: contractAward.effective_date.toString(),
      award_version_date: contractAward.contract_award_type.toString(),
      mod_order: contractAward.modification_order.toString(),

      previous_contract: {
        previous_contract_number: currentContractInfo.contract_number.toString(),
        pervious_to_number: currentContractInfo.task_delivery_order_number.toString()
      },
      to_title: projectOverview.title.toString(),
      scope: projectOverview.scope.toString(),
      scope_surge: requirementsCostEstimate.surge_capabilities.toString(),

      // need to mindful if properties are undifined
      current_environment: {
        current_environment_exists: 
        	ctb(currentEnvironment.current_environment_exists.toString()),
		environment_instances: instances,
        additional_info: currentEnvironment.additional_information.toString(),
      },

      required_services: services,

      period_of_performance: {
      	base_period: packagePeriodsById[periodOfPerformance.base_period.toString()],
        option_periods: packageOptionPeriods,
        pop_start_request: ctb(periodOfPerformance.pop_start_request.toString()),
        requested_pop_start_date: periodOfPerformance.requested_pop_start_date.toString(),
        time_frame: periodOfPerformance.time_frame.toString(),
        recurring_requirement: ctb(periodOfPerformance.recurring_requirement.toString())
      },

      gfe_overview: {
      	gfe_or_gfp_furnished: ctb(gfeOverview.gfe_gfp_furnished.toString()),
        property_accountable: ctb(gfeOverview.property_accountable.toString()),
        dpas_unit_id: gfeOverview.dpas_unit_identification_code.toString(),
        dpas_custodian_number: gfeOverview.dpas_custodian_number.toString(),
        property_custodian_name: gfeOverview.property_custodian_name.toString()
      },

      contract_considerations: {
        packaging_shipping_none_apply: cc.packaging_shipping_none_apply.toString(),
        packaging_shipping_other: cc.packaging_shipping_other.toString(),
        packaging_shipping_other_explanation: 
        	cc.packaging_shipping_other_explanation.toString(),
        potential_conflict_of_interest: ctb(cc.potential_conflict_of_interest.toString()),
        conflict_of_interest_explanation: cc.conflict_of_interest_explanation.toString(),
        contractor_provided_transfer: cc.contractor_provided_transfer.toString(),
        contractor_required_training: ctb(cc.contractor_required_training.toString()),
        required_training_services: cc.required_training_courses.toString()
      },

      section_508_accessibility_standards: {
		pii_present: ctb(sensitiveInfo.pii_present.toString()),
        work_to_be_performed: sensitiveInfo.work_to_be_performed.toString(),
        system_of_record_name: sensitiveInfo.system_of_record_name.toString(),
        FOIA_city_apo_fpo: sensitiveInfo.foia_city_apo_fpo.toString(),
        FOIA_country: sensitiveInfo.foia_country.toString(),
        FOIA_street_address_1: sensitiveInfo.foia_street_address_1.toString(),
        FOIA_street_address_2: sensitiveInfo.foia_street_address_2.toString(),
        FOIA_address_type: sensitiveInfo.foia_address_type.toString(),
        FOIA_zip_postal_code: sensitiveInfo.foia_zip_postal_code.toString(),
        FOIA_state_province_code: sensitiveInfo.foia_state_province_state_code.toString(),
        FOIA_full_name: sensitiveInfo.foia_full_name.toString(),
        FOIA_email: sensitiveInfo.foia_email.toString(),
        BAA_required: ctb(sensitiveInfo.baa_required.toString()),
        potential_to_be_harmful: ctb(sensitiveInfo.potential_to_be_harmful.toString()),
        section_508_sufficient: ctb(sensitiveInfo.section_508_sufficient.toString()),
        accessibility_reqs_508: sensitiveInfo.accessibility_reqs_508.toString()
      }
    };

  outputs.dow_data = JSON.stringify(dow_document_data);

})(inputs, outputs);

