sys_variable_value.value:
(function execute(inputs, outputs) {
  var del_record = new GlideSysAttachment();
  var att = del_record.getAttachments('sys_ws_operation','608ff756db7055d08c045e8cd396197f');
  while(att.next()) {
    del_record.deleteAttachment(att.sys_id);
  }

  att = del_record.getAttachments('sys_ws_operation','c65f7756db7055d08c045e8cd39619bf');
  while(att.next()) {
    del_record.deleteAttachment(att.sys_id);
  }

  att = del_record.getAttachments('sys_ws_operation','042fbf9edb7055d08c045e8cd39619d0');
  while(att.next()) {
    del_record.deleteAttachment(att.sys_id);
  }
})(inputs, outputs);

(function execute(inputs, outputs) {
  var request = new sn_ws.RESTMessageV2();
  var errUtil = new ErrorHandler();
  var response;
  var token; 

  // get the GitHub PAT from the Credentials
  var provider = new sn_cc.StandardCredentialsProvider();
  var sys_id = gs.getProperty("x_g_dis_atat.github.credential.sys_id");
  var credential = provider.getAuthCredentialByID(sys_id);
  if (credential) {
    token = credential.getAttribute("api_key")
  } else {
    gs.error("Failed to retrieve credential for 'Make Request'");
  }

  // set headers
  var contentType = "application/vnd.github+json";
  var table_name = "x_g_dis_atat_cd_artifacts";
  var attachment_sys_id = "";
  var filename = "release.zip"

  var destination_record = new GlideRecord(table_name);
  var record_sys_id = destination_record.insert();

  try {
    // Request set-up
    request.setEndpoint(inputs.Uri);
    request.setHttpMethod("GET");
    request.setLogLevel("all");
    request.setRequestHeader("Authorization", "Bearer " + token);
    request.setRequestHeader("Content-Type", contentType);
    request.setRequestHeader("X-GitHub-Api-Version", "2022-11-28");
    request.contentType = contentType;

    request.saveResponseBodyAsAttachment(table_name, record_sys_id, filename);

    response = request.execute();
  } catch (error) {
    errUtil.errorLogger(error);
  }

  if (response.getStatusCode() != 200) {
    gs.error(response.getErrorMessage())
  } else {
    var attachmentGR = new GlideRecord('sys_attachment');
    attachmentGR.addQuery('table_name', table_name);
    attachmentGR.addQuery('table_sys_id', record_sys_id);
    attachmentGR.orderByDesc('sys_created_on');
    attachmentGR.query();

    if (attachmentGR.next()) {
      attachment_sys_id = attachmentGR.getValue('sys_id');
    } else {
      gs.error(response.getErrorMessage())
    }  
    outputs.attachment_id = attachment_sys_id;
    outputs.target_record = record_sys_id;
  }

})(inputs, outputs);

