sys_variable_value.value:
(function execute(inputs, outputs) {
// remove all records from x_g_dis_atat_cd_artifacts
  var record = new GlideRecord('sys_attachment');
  record.addQuery('table_name','x_g_dis_atat_cd_artifacts');
  record.query();

  while (record.next()) {
    record.deleteRecord();
  }

  var artifact_record = new GlideRecord('x_g_dis_atat_cd_artifacts');
  artifact_record.query();

  while (artifact_record.next()) {
    artifact_record.deleteRecord();
  }
})(inputs, outputs);

