sys_variable_value.value:
(function execute(inputs, outputs) {
  var sbom;
  var att_root = new GlideSysAttachment();
  var att = att_root.getAttachments('x_g_dis_atat_cd_artifacts', inputs.artifact_record_sys_id);
  while (att.next()) {
    if (att.file_name == 'sbom.txt') {
      sbom = att_root.getContent(att);
      break;
    }
  }

  var file_paths = sbom.split(/\r?\n/);
  var file_lookup = {};

  for (var i = 0; i < file_paths.length; i++) {
    var path_parts = file_paths[i].split('/');
    if (path_parts.length < 3) {
      continue;
    }
    var filename = path_parts[path_parts.length-1];
    var folder = path_parts[path_parts.length-2];

    // for each subfolder of dist/[js|img|other_assets], move files from sys_attachments to matching destination 
    // JS = sys_ws_operation/608ff756db7055d08c045e8cd396197f
    // IMG = sys_ws_operation/c65f7756db7055d08c045e8cd39619bf
    // other assets = sys_ws_operation/042fbf9edb7055d08c045e8cd39619d0
    if (folder.toLowerCase() === "js") {
      file_lookup[filename] = '608ff756db7055d08c045e8cd396197f';
    } else if ( folder.toLowerCase() == "img") {
      file_lookup[filename] = 'c65f7756db7055d08c045e8cd39619bf';
    } else if ( folder.toLowerCase() == "other_assets") {
      file_lookup[filename] = '042fbf9edb7055d08c045e8cd39619d0';
    } else {
      gs.warn("Not moving " + folder + "/" + filename);
    }
  }

  var attachment_root = new GlideSysAttachment();
  var attachment = attachment_root.getAttachments('x_g_dis_atat_cd_artifacts', inputs.artifact_record_sys_id);
  while (attachment.next()) {
    // get the attatchment filename and look it up in 'folders'
    var target = new GlideRecord('sys_ws_operation');
    var filename = attachment.file_name;
    if (file_lookup[filename]) { 
      target.get(file_lookup[filename])
      var contentType = attachment.content_type;
      var new_att = new GlideSysAttachment();
      var content = new_att.writeContentStream(target, filename, contentType, new_att.getContentStream(attachment.sys_id));
    }
  }
})(inputs, outputs);

