sys_variable_value.value:
function addCostRecord(result, csp, organization, portfolio, taskOrder, clin, isActual) {
  var gdt = new GlideDateTime();
  gdt.setDisplayValue(result.month, "yyyy-MM");

  // Check to see if we already have a record for this Task Order / CLIN / Year-Month Tuple
  var cost = new GlideRecord('x_g_dis_atat_costs');
  cost.addQuery("task_order_number", taskOrder.task_order_number);
  cost.addQuery("clin", clin.clin_number);
  cost.addQuery("year_month", gdt.getDate());
  cost.query();

  var isUpdate = false;
  if (cost.hasNext()) {
    cost.next();
    isUpdate = true;       
  }
  else {
    cost.newRecord();
    isUpdate = false;       
  }

  cost.clin = clin.clin_number;
  cost.csp = csp.sys_id;
  cost.is_actual = isActual;
  cost.organization = organization.sys_id;
  cost.portfolio = portfolio.sys_id;
  cost.service_agency = organization.service_agency;
  cost.task_order_number = taskOrder.task_order_number;
  cost.value = result.value;
  cost.year_month = gdt;

  if (isUpdate) {
    cost.update();
  }
  else {
    cost.insert();
  }

}

function handleCostObject(costObject) {
  var csp = new GlideRecord('x_g_dis_atat_cloud_service_provider');
  csp.get("name", costObject.content.request.targetCsp.name);

  var portfolio = new GlideRecord('x_g_dis_atat_portfolio');
  portfolio.get("csp_portfolio_id", costObject.content.request.portfolioId);

  var organization = portfolio.acquisition_package.organization;

  var taskOrders = costObject.content.response.task_orders;
  for (var i = 0; i < taskOrders.length; i++) {
    var taskOrder = taskOrders[i];
    var clins = taskOrder.clins;
    for (var j = 0; j < clins.length; j++) {
      var clin = clins[j];

      var actuals = clin.actual;
      for (var k = 0; k < actuals.length; k++) {
        var actual = actuals[k];
        var actual_results = actual.results;
        for (var l = 0; l < actual_results.length; l++) {
          addCostRecord(actual_results[l], csp, organization, portfolio, taskOrder, clin, true);
        }      
      }

      var forecasts = clin.forecast;
      for (var k = 0; k < forecasts.length; k++) {
        var forecast = forecasts[k];
        var forecast_results = forecast.results;
        for (var l = 0; l < forecast_results.length; l++) {
          addCostRecord(forecast_results[l], csp, organization, portfolio, taskOrder, clin, false);
        }        
      }      
    }
  }  
}

(function execute(inputs, outputs) {
  var costObjects = JSON.parse(inputs.cost_response);
  for (var i = 0; i < costObjects.length; i++) {
	handleCostObject(costObjects[i]);
  }
})(inputs, outputs);

