sys_variable_value.value:
(function execute(inputs, outputs) {

  var attachments = new PackageAttachments(inputs.acqPackage);

  // attchment documents from below records (source)
  var unsignedTable = "x_g_dis_atat_package_documents_unsigned";
  var unsignedSysId = attachments.unsignedRecSysId;
  var signedTable = "x_g_dis_atat_package_documents_signed";
  var signedSysId = attachments.signedRecSysId;
  outputs.funding_form_sys_id = attachments.miprSysId ? attachments.miprSysId : attachments.fsFormSysId;

  // attachment documents to the email record to be sent (target)
  var emailTable = "sys_email";
  var emailSysId = inputs.emailSysId;
  var sysAttachments = new GlideSysAttachment();

  // copy unsigned documents to email
  sysAttachments.copy(unsignedTable, unsignedSysId, emailTable, emailSysId);

  // copy signed documents to email
  sysAttachments.copy(signedTable, signedSysId, emailTable, emailSysId);

})(inputs, outputs);

