sys_variable_value.value:
(function execute(inputs, outputs) {

  outputs.docusign_recipients = []
  while (inputs.contacts.next()) {
    var contact = inputs.contacts;
    var order, roleName;
    switch (contact.type.toString()) {
      case "MISSION_OWNER":
        order = 1
        roleName = "Mission Owner"
        break
      case "JWCC_CONTRACTING_OFFICER":
        order = 2
        roleName = "JWCC Contracting Officer"
        break
      case "FINANCIAL_POC":
        order = 3
        roleName = "Financial POC"
        break
      default:
        order = undefined
        roleName = undefined
        continue
    }

    outputs.docusign_recipients.push({
      name: contact.formal_name.toString(),
      email: contact.email.toString(),
      role_name: roleName,
      recipient_id: order.toPrecision(1),
      contact_type: contact.type.getDisplayValue(),
      recipient_type: "signers"
    })  
  }

})(inputs, outputs);

