sys_variable_value.value:
(function execute(inputs, outputs) {

  var attachments = new PackageAttachments(inputs.acqPackage);
  attachments.gatherUnsignedDocumentsForDownload();
  // documents related to funding request are looked up directly in the sys_attachment table 
  // because of the "ZZ_YY" prefix when using a file attachment column type, which appears 
  // to not work with the GlideSysAttachment.copy() method (unless there is a permissions issue
  // blocking the action and silently failing. As a result, the funding documents are copied 
  // in flow using the copy attachment action
  outputs.funding_form_sys_id = attachments.miprSysId ? attachments.miprSysId : attachments.fsFormSysId;

})(inputs, outputs);

