sys_variable_value.value:
(function execute(inputs, outputs) {

  var emailSysId = inputs.targetSysId;
  var attachments = new EmailAttachments(inputs.acqPackage);
  gs.info("UNSIGNED REC" + attachments.unsignedRecSysId)
  gs.info("SIGNED REC" + attachments.signedRecSysId)
  gs.info("REQ COST EST: " + attachments.reqCostEstRecSysId)
  gs.info("CURR ENV: " + attachments.currentEnvRecSysId)
  attachments.copyDocumentsToEmail(emailSysId);
   // UNSIGNED_DOCS_TABLE: "x_g_dis_atat_package_documents_unsigned",
  //SIGNED_DOCS_TABLE: "x_g_dis_atat_package_documents_signed",


  var unsignedTable = "x_g_dis_atat_package_documents_unsigned";
  var unsignedSysId = attachments.unsignedRecSysId;
  var signedTable = "x_g_dis_atat_package_documents_signed";
  var signedSysId = attachments.signedRecSysId;
  outputs.funding_form_sys_id = attachments.miprSysId ? attachments.miprSysId : attachments.fsFormSysId;
  outputs.requirements_cost_est_sys_id = attachments.reqCostEstRecSysId;

  // attachment target
  var emailTable = "sys_email" //inputs.targetTable;
  var emailSysId = inputs.targetSysId;
  var attachmentsUtil = new GlideSysAttachment();

  // copy unsigned documents to email
  attachmentsUtil.copy(unsignedTable, unsignedSysId, emailTable, emailSysId);

  // copy signed documents to email
  attachmentsUtil.copy(signedTable, signedSysId, emailTable, emailSysId);

  //var attachments = new GlideSysAttachment();
  //attachments.copy(inputs.sourceTable, inputs.sourceSysId, inputs.targetTable, inputs.targetSysId);

})(inputs, outputs);

