sys_hub_input_scripts.script:
{"ah_body":{"scriptActive":true,"script":"const gr = new GlideRecord('x_g_dis_atat_environment');
gr.addQuery('portfolio', fd_data.trigger.current.sys_id);
gr.query();
let cls = [];
while(gr.next()) {
 const classification_level = gr['classification_level'].getDisplayValue();
 cls.push(classification_level);
}

const updatedByUserId = fd_data.trigger.current.sys_updated_by;
let updatedByUserName;
let updatedByUserEmail;
const userGr = new GlideRecord('sys_user');
userGr.addQuery('user_name', updatedByUserId);
userGr.query();
if (userGr.next()) {
 updatedByUserName = userGr.name;
 updatedByUserEmail = userGr.email;
} else {
 throw new Error(`User with ID ${updatedByUserId} not found.`);
}

const variableMap = {
 'agencyName': fd_data.trigger.current.agency.title,
 'classificationLevels': cls.toString(),
 'cspName': fd_data.trigger.current.vendor.getDisplayValue().toString(),
 'pocEmail': updatedByUserEmail,
 'pocName': updatedByUserName,
 'portfolioTitle' : fd_data.trigger.current.name,
 'taskOrderNumber': fd_data.trigger.current.active_task_order.task_order_number,
 'totalObligatedAmount': fd_data.trigger.current.active_task_order.funds_obligated,
 'totalTaskOrderAmount': fd_data.trigger.current.active_task_order.funds_total
};

return `
<h2> New ATAT portfolio started provisioning. </h2>
<p>${variableMap[`agencyName`]} started provisioning the "<b>${variableMap[`portfolioTitle`]}</b>" portfolio within <b>${variableMap[`cspName`]}</b>. Please review the following task order information:</p>
<ul>
 <li>Task Order Number: #${variableMap[`taskOrderNumber`]}</li>
 <li>Total Obligated Amount: $${variableMap[`totalObligatedAmount`]}</li>
 <li>Total Task Order Amount: $${variableMap[`totalTaskOrderAmount`]}</li>
 <li>Classification/Impact Level(s): ${variableMap[`classificationLevels`]}</li>
</ul>
For more information regarding this portfolio, please contact ${variableMap[`pocName`]} at ${variableMap[`pocEmail`]}.
<br><br>
<small>This was sent from an unmonitored email address. Please do not reply to this email.</small>`;"}}

