sys_hub_input_scripts.script:
{"ah_body":{"scriptActive":true,"script":"const updatedByUserId = fd_data.trigger.current.sys_updated_by;
const portfolioName = fd_data.trigger.current.name;
let updatedByUserName;
const gr = new GlideRecord('sys_user');
gr.addQuery('user_name', updatedByUserId);
gr.query();
if (gr.next()) {
 updatedByUserName = gr.name;
} else {
 throw new Error(`User with ID ${updatedByUserId} not found.`);
}

const managerPrivileges = `
* Track cloud usage and funds spent on portfolio task order(s)
* Request to modify task orders
* Grant administrator access to the CSP console
* Manage portfolio members within ATAT`;

return `
${updatedByUserName} invited you to manage the "${portfolioName}" portfolio within the Account Tracking Automation Tool (ATAT). 

You can now:
${managerPrivileges}

Access the portfolio from your dashboard: https://services.disa.mil/atat-dapps 

This was sent from an unmonitored email address. Please do not reply to this email. If you have questions about JWCC or DAPPS, please contact our Customer Support Team at HaCC Community.`"}}

{"ah_to":{"scriptActive":true,"script":"const changedFields = fd_data.trigger.changed_fields;
let newUsers = [];
for(let i=0; i<changedFields.length; ++i) {
	const changedField = changedFields[i]["field_name"];
	if(changedField === `portfolio_managers`) {
		const previousUsers = changedFields[i]["previous_value"].split(',');
		const currentUsers = changedFields[i]["current_value"].split(',');
	 newUsers = currentUsers.filter(function(item) {
				return !previousUsers.includes(item); 
				});		
		break;
	}
}

let emails = [];
for(let i=0; i<newUsers.length; ++i) {
	const newUser = newUsers[i];
	const gr = new GlideRecord('sys_user');
	gr.addQuery('sys_id', newUser);
	gr.query();
	while (gr.next()) {
		emails.push(gr.email);
	}
}
if(emails.length === 0) {
	gs.error(`Error; failed to find a new portfolio manager.`);
	throw `Error; failed to find a new portfolio manager.`; 
}

return emails.toString();

"}}

