var pocTitleAndName = gr.formal_name.toString();
var pocEmail = gr.email.toString();

var emailBody = "A JWCC requirements package was submitted for review. All completed templates, signed documents, and supporting documentation are attached.";
emailBody += "For more information regarding this package, please contact " + pocRankAndName + " at " + pocEmail + ".";"}}

