sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Toast message when updating the record in the server-side UI
	var message = "Not allowed to update 'Portfolio Managers' to be an empty list.";
	gs.addInfoMessage(message);

	// System log
	var portfolio = current.name + " portfolio (Sys ID: " + current.sys_id + ") ";
	message += " The " + portfolio + " managers list was not updated with the current value."; 
	gs.warn(message);

	if (previous.portfolio_managers.toString() === "") {
		// System log 
		// if the portfolio was created without any managers assigned
		var secondMessage = "No Portfolio Mangers were added to the " + portfolio;
		secondMessage += " during creation. The current user ";
		secondMessage += gs.getUserName() + " (Sys ID: " + gs.getUserID() + ") ";
		secondMessage += "is being added as a default. Update with intended managers.";
		gs.warn(secondMessage);

		current.portfolio_managers = gs.getUserID();

	} else {
		gs.info("The " + portfolio + " was updated with the previous managers list.");
		current.portfolio_managers = previous.portfolio_managers;	
	}


})(current, previous);
