sys_script.script:
// this business rule ensures 
// - if a user is added to portfolio_manager, they are removed from portfolio_viewer
// - if a user is added to portfolio_viewer, they are removed from portfolio_manager
// 
// this business rule does not handle portfolio_owner; portfolio_owner is a reference not a list.
// other business logic will explicitly ensure that portfolio_owner state is always valid.
(function executeRule(current, previous) {
    const portfolioSysId = current.sys_id;
    if (current.portfolio_viewers.changes()) {
        const changes = getChangeBetweenTwoLists(previous.portfolio_viewers, current.portfolio_viewers);
        updateDb(portfolioSysId, changes, 'portfolio_managers');
    }

    if (current.portfolio_managers.changes()) {
        const changes = getChangeBetweenTwoLists(previous.portfolio_managers, current.portfolio_managers);
        updateDb(portfolioSysId, changes, 'portfolio_viewers');
    }
})(current, previous);

// utility method to get the diff between two lists
function getChangeBetweenTwoLists(l1, l2) {
    const l1Split = l1.toString().split(',');
    const l2Split = l2.toString().split(',');
    return l2Split.filter(function(item) {
        return !l1Split.includes(item);
    });
}

function updateDb(portfolioSysId, diff, type) {
    diff.forEach(function(user) {
        const gr = new GlideRecord('x_g_dis_atat_portfolio');
        gr.addQuery('sys_id', portfolioSysId);
        gr.addQuery(type, 'CONTAINS', user);
        gr.query();
        while (gr.next()) {
            const splitList = gr.getValue(type).split(',');
            const index = splitList.indexOf(user);
            if (index > -1) {
                splitList.splice(index, 1);
                gr.setValue(type, splitList.join(','));
                gr.update();
            }
        }
    });
}
