sys_script.script:
(function executeRule(current, previous) {
    if (current.portfolio_owner) gs.info("owner: " + current.portfolio_owner);
    if (current.portfolio_managers) gs.info("managers: " + current.portfolio_managers);
    if (current.portfolio_viewers) gs.info("viewers: " + current.portfolio_viewers);

    // Owner changed
    if (current.portfolio_owner.changes()) {
        if (current.portfolio_managers) {
            const managers = current.portfolio_managers.toString().split(',');
            if (managers.includes(current.portfolio_owner.toString())) {
                gs.addErrorMessage(current.portfolio_owner.name + " is already a Portfolio Manager. Update cancelled.");
                current.setAbortAction(true);
            }
        }
        if (current.portfolio_viewers) {
            const viewers = current.portfolio_viewers.toString().split(',');
            if (viewers.includes(current.portfolio_owner.toString())) {
                gs.addErrorMessage(current.portfolio_owner.name + " is already a Portfolio Viewer. Update cancelled.");
                current.setAbortAction(true);
            }
        }
    }

    // Managers changed
    if (current.portfolio_managers.changes()) {
        // TODO if owner contains any of the new managers then halt the update
        // TODO if viewers contains any of the new managers then halt the update
    }

    // Viewers changed
    if (current.portfolio_viewers.changes()) {
        // TODO if owner contains any of the new viewers then halt the update
        // TODO if managers contains any of the new viewers then halt the update
    }
})(current, previous);
