sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var gdt = new GlideDateTime();
	var currentDate = gdt.getDate();

	var dateArray = currentDate.toString().split("-");

	var currentYear = dateArray[0].slice(-2);
	var currentMonth = dateArray[1];
	var currentDay = dateArray[2];

	var fileName = "DAPPS Acquisition Package Report (" + currentMonth + "-" + currentDay + "-" + currentYear + ").csv";

	var content = new GlideSysAttachment().getContentStream(current.sys_id);
	new GlideSysAttachment().writeContentStream(current, fileName, current.getValue('content_type'), content);

})(current, template, email, email_action, event);
