sys_script_include.script:
function convertToBoolean(input) {
    var receivedType = '';
    switch (typeof(input)) {
        case 'boolean': //nothing to do
            return input;
        case 'string':
            var lowercase = input.toLowerCase();
            if (lowercase === "yes" || lowercase === "true") return true;
            if (lowercase === "no" || lowercase === "false") return false;
            gs.debug('Function convertToBoolean() recieved a string parameter with unexpected value \'' + input + '\'');
            return null;
        default:
            if (typeof(input) === 'object') {
                if (input === null) {
                    receivedType = 'null';
                } else {
                    receivedType = input.constructor.name;
                }
            }
            if (input === undefined) {
                receivedType = 'undefined';
            }
            gs.debug('Function convertToBoolean() expects a string or boolean parameter.  Recieved ' + receivedType + ' instead.');
            return null;
    }
}
