sys_script_include.script:
class AlertProcessor {

    constructor() {
		this.ALERT_TYPE_FUNDS = "funds";
		this.ALERT_TYPE_TIME = "time";

        this.PREFERENCES_TABLE = "x_g_dis_atat_preferences";
        this.USER_TABLE = "sys_user";
        this.TRACKING_TABLE = "x_g_dis_atat_alerts_tracking";

		this.QUEUE_FUNDS = "x_g_dis_atat.alert_funds";
		this.QUEUE_TIME = "x_g_dis_atat.alert_time";
    }

    process() {
        const processed = this.getTrackingData();
        Object.keys(processed).forEach(clin => {
            Object.keys(processed[clin]).forEach(group => {
                this.updateTrackingRecords(processed[clin][group]);
            });
        });
        return processed;
    }

    getTrackingData() {
        return new global.GlideQuery(this.TRACKING_TABLE)
            .where('status', 'new')
            .orderBy('clin')
            .orderBy('alert.group')
            .orderBy('alert.priority')
            .select('alert.group', 
					'alert.sys_id', 
					'alert.threshold', 
					'alert.type',
					'portfolio$DISPLAY', 
					'clin', 
					'clin$DISPLAY', 
					'sys_id')
            .toArray(100)
            .reduce((r, a) => {
                r[a.clin] = r[a.clin] || {};
                r[a.clin][a.alert.group] = r[a.clin][a.alert.group] || [];
                r[a.clin][a.alert.group].push(a);
                return r;
            }, {});
    }

    updateTrackingRecords(records) {
        const gr = new GlideRecord(this.TRACKING_TABLE);
        if (gr.get(records[0].sys_id)) {
            this.processAlert(gr, records[0]);
            gr.status = 'processed';
            gr.update();
        }

        records.slice(1).forEach(record => {
            if (gr.get(record.sys_id)) {
                gr.status = 'omitted';
                gr.update();
            }
        });
    }

	// TODO: remove users that opt-out
	// TODO: generalize type?
    processAlert(gr, record) {
        const userSysIds = this.extractUserSysIds(gr);
		const queue = (record.alert.type === this.ALERT_TYPE_FUNDS) ? this.QUEUE_FUNDS : this.QUEUE_TIME;
        const emails = new global.GlideQuery(this.USER_TABLE)
            .where('sys_id', 'IN', userSysIds)
            .select('email')
            .toArray(100)
            .forEach((user) => {
				gs.eventQueue(queue, gr, user.email, gr.portfolio.active_task_order.getDisplayValue());
			});
    }

    extractUserSysIds(gr) {
        const emails = gr.portfolio.portfolio_owner.toString();
		if(gr.portfolio.portfolio_managers) {
			emails.concat(gr.portfolio.portfolio_managers.toString().split(','));
		}
		if(gr.portfolio.portfolio_viewers) {
			emails.concat(gr.portfolio.portfolio_viewers.toString().split(','));
		}

        return [].concat(emails);
    }
}
