sys_script_include.script:
function monthsInPeriod(unit, unit_count) {
	var months_in_period;
	if (unit == "DAY"){
		// months = days × 0.032855
		months_in_period = Math.ceil(parseInt(unit_count * 0.032855));
	}
	if (unit == "WEEK"){
		// months = weeks × 0.229984
		months_in_period = Math.ceil(parseInt(unit_count * 0.229984));
	}
	if (unit == "MONTH"){
		// months = months
		months_in_period = unit_count;
	}
	if (unit == "YEAR"){
		// months = years × 12
		months_in_period = parseInt(unit_count * 12);
	}
	return months_in_period;

}
