sys_security_acl.script:
var userRoles = gs.getUser().getRoles().toString().split(',');
var up = new UserPermissions(gs.getUserID(), userRoles);

var accessList;
var acqPackage = new GlideRecord(up.ACQ_PKG_TABLE);
acqPackage.addQuery("selected_service_offerings", "CONTAINS", current.sys_id);
acqPackage.query();

if(acqPackage.hasNext()) {
   acqPackage.next();
   accessList = acqPackage.mission_owners.toString().split(",");
}

answer = up.hasReadPermission(accessList);
