sys_security_acl.script:
var userRoles = gs.getUser().getRoles().toString().split(',');
var up = new UserPermissions(gs.getUserID(), userRoles);
var acqPackage;

// go up one level
var parentRecord = new GlideRecord("x_g_dis_atat_selected_service_offering");
parentRecord.addQuery("estimated_environment_instances", "CONTAINS", current.sys_id);
parentRecord.query();
if (parentRecord.hasNext()) {
  parentRecord.next();
  // up one more level 
  var grandparentRecord = new GlideRecord("x_g_dis_atat_acquisition_package");
  grandparentRecord.addQuery("selected_service_offerings", "CONTAINS", parentRecord.sys_id);
  grandparentRecord.query();
  if (grandparentRecord.hasNext()) {
    grandparentRecord.next();
    acqPackage = grandparentRecord;
    gs.info(grandparentRecord.number);
    gs.info(grandparentRecord.sys_id);
  }
}

var accessList = acqPackage.mission_owners.split(",");
answer = up.hasReadPermission(accessList);
