sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var rec = new GlideRecord("x_g_dis_zach_widge_poc_widget");
	// This query returns all records in the table
	rec.query();
	// Set up an array to return to user
	var arrayOfRecs= [];
	// Go through each record and add the name, description, and size to it
	while(rec.next()){
		// this is the record, wrapped in a simple JSON object for clarity
		var record = {};
		record.name = rec.getDisplayValue("name");
		record.description = rec.getDisplayValue("description");
		record.size = rec.getDisplayValue("size");
		arrayOfRecs.push(record);
	}
	// Set response to application/json
	// Use writer to make it strictly json
	//
	response.setContentType("application/json");
	var writer = response.getStreamWriter();
	writer.writeString(JSON.stringify(arrayOfRecs));
	//
	// OR
	// Return it wrapped in a response object, this is how they do it in the docs
	// response.setBody(arrayOfRecs);

})(request, response);
