sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var body = request.body.data;
	// Validate request body
	if (!body.name || !body.description || !body.size) {
		response.setError(new sn_ws_err.BadRequestError("Missing Required Fields"));
	}
	else {
	// Create a new Widget
		var widget = new GlideRecord("x_g_dis_zach_widge_poc_widget");
		widget.setValue("name", request.body.data.name);
		// widget.setValue("description", request.body.data.description);
		widget.setValue("size", request.body.data.size);
		// Journal field in SNOW is strange, check out the docs, it doesn't work with setValue
		widget.description = request.body.data.description;
		widget.insert();
		// Set up response object
		var res = {
			"name": request.body.data.name,
			"description": request.body.data.description,
			"size": request.body.data.size
		};
		response.setBody(res);
	}	

})(request, response);
